#include "gfxController.hpp"


AQM1248A::AQM1248A(SPIClass SPI,uint8_t res,uint8_t rs) : _spi(SPI) {
    _res = res;
    _rs = rs;


    pinMode(_res,OUTPUT);
    pinMode(_rs,OUTPUT);

    _settings = SPISettings(2000000, MSBFIRST, SPI_MODE3);
}

void AQM1248A::clear(void){
    for(uint8_t page=0; page<6; page++) {
        memset(&_gfxBuffer[page][0],0x00,128);
        writeCommand(0xb0+page);
        writeCommand(0x10);
        writeCommand(0x00);
        for(uint8_t x=0; x<128; x++) {
            writeData(0);
        }
    }
}
void AQM1248A::init(void){
    digitalWrite(_res,LOW);

    SPI.begin();
    SPI.beginTransaction(_settings);

    digitalWrite(_res,HIGH);

    writeCommand(0xae);
    writeCommand(0xa0);
    writeCommand(0xc8);
    writeCommand(0xa3);

    writeCommand(0x2c);
    delay(2);
    writeCommand(0x2e);
    delay(2);
    writeCommand(0x2f);

    writeCommand(0x23);
    writeCommand(0x81);
    writeCommand(0x1c);

    writeCommand(0xa4);
    writeCommand(0x40);
    writeCommand(0xa6);
    writeCommand(0xaf);
}

void AQM1248A::xferAll(void){
    for(uint8_t page=0; page<6; page++) {
        writeCommand(0xb0+page);
        writeCommand(0x10);
        writeCommand(0x00);
        for(uint8_t x=0; x<128; x++) {
            writeData(_gfxBuffer[page][x]);
        }
    }
}

void AQM1248A::xferPage(uint8_t page){
    writeCommand(0xb0+page);
    writeCommand(0x10);
    writeCommand(0x00);
    for(uint8_t x=0; x<128; x++) {
        writeData(_gfxBuffer[page][x]);
    }
}

void AQM1248A::writeCommand(uint8_t cmd){
    digitalWrite(_rs,LOW);
    SPI.transfer(cmd);
}
void AQM1248A::writeData(uint8_t dat){
    digitalWrite(_rs,HIGH);
    SPI.transfer(dat);
}

void AQM1248A::setPixel(uint8_t x,uint8_t y,uint8_t state){
    uint8_t page = y >> 3;
    uint8_t row = x;
    uint8_t col = y % 8;

    if(state){
        //set
        _gfxBuffer[page][row] |= 1 << col;
    } else {
        //clear
        _gfxBuffer[page][row] &= ~(1 << col);
    }

}
