void AQM1248A::showState(uint8_t state){
    if(state ==  1){
        for(uint8_t y = 0;y < 16;y++){
            for(uint8_t x = 0;x < 16;x++){
                if(pat_play[y] & (1 << (15-x))){
                    setPixel(x,32+y,1);
                } else {
                    setPixel(x,32+y,0);
                }
            }
        }
    } else {
        for(uint8_t y = 0;y < 16;y++){
            for(uint8_t x = 0;x < 16;x++){
                if(pat_pause[y] & (1 << (15-x))){
                    setPixel(x,32+y,1);
                } else {
                    setPixel(x,32+y,0);
                }
            }
        }
    }
    xferAll();
}

void AQM1248A::showVolume(uint8_t vol){

    for(uint8_t y = 0;y < 16;y++){
        for(uint8_t x = 0;x < 40 ;x++){
                setPixel(x+80,32+y,0);
        }
    }

    for(uint8_t y = 0;y < 16;y++){
        for(uint8_t x = 0;x < 5 * vol;x++){
            if(pat_vol[y][x]){
                setPixel(x+80,32+y,1);
            } else {
                setPixel(x+80,32+y,0);
            }
        }
    }
    xferAll();
}

void AQM1248A::showSplash(void){
    clear();
    for(uint8_t y = 0;y < 48;y++){
        for(uint8_t x = 0;x < 128;x++){
            if(pat_splash[y][x]){
                setPixel(x,y,1);
            } else {
                setPixel(x,y,0);
            }
        }
    }
    xferAll();
}